class SubMainPage extends StatefulWidget {
  final Store<AppReducerState> store;

  SubMainPage(this.store, {Key? key}) : super(key: key);
  @override
  State<StatefulWidget> createState() => _SubMainPageState();
}

class _SubMainPageState extends State<SubMainPage>
    with SingleTickerProviderStateMixin, AutomaticKeepAliveClientMixin {
  var controller = ScrollController();

  var comicContinue = ComicCardContiuneReadingModel(
      "testId",
      "Joker War",
      "",
      "Chapter 56");

  UserModelB? userModelb;
  //will change
  List<ComicCardModelA> trendComicList = [];
  List<ComicCardLikedModel> likedComicList = [];

  bool isUserLoading = true;
  bool userError = false;
  bool isTrendComicsLoading = true;
  bool trendComicsError = false;
  bool islikedComicsLoading = true;
  bool likedComicsError = false;

  void buildBottomModals(context) {
    TopReadersModal(widget.store, context);
    ContinueReadingModal(context, comicContinue);
  }

  @override
  void initState() {
    super.initState();

    if (widget.store.state.userReducerState!.user != null) {
      userModelb = widget.store.state.userReducerState!.user;
      isUserLoading = false;
    }

    if (widget.store.state.comicsReducerState!.comics != null) {
      trendComicList = widget.store.state.comicsReducerState!.comics!.toList();
      isTrendComicsLoading = false;
    }

    if (widget.store.state.likedComicsReducerState!.comics != null) {
      likedComicList =
          widget.store.state.likedComicsReducerState!.comics!.toList();
      islikedComicsLoading = false;
    }

    widget.store.onChange.listen((event) {
      setState(() {
        //user
        if (event.userReducerState!.isLoading != true) {
          userModelb = event.userReducerState!.user;
          isUserLoading = false;
        }
        if (event.userReducerState!.isError == true) {
          userError = true;
        }
        //user

        //comic
        if (event.comicsReducerState!.isLoading != true &&
            event.comicsReducerState!.comics != null) {
          isTrendComicsLoading = false;
          trendComicList = event.comicsReducerState!.comics!.toList();
        } else if (event.comicsReducerState!.isError == true) {
          trendComicsError = true;
        }
        //comic

        //liked comic
        if (event.likedComicsReducerState!.isLoading != true &&
            event.likedComicsReducerState!.comics != null) {
          islikedComicsLoading = false;
          likedComicList = event.likedComicsReducerState!.comics!.toList();
        } else if (event.likedComicsReducerState!.isError == true) {
          likedComicsError = true;
        }
        //liked comic
      });
    });

    controller.addListener(onScroll);
  }

  void toProfilePage() {
    Navigator.of(context).push(myProfilePageRoute());
  }

  //scroll animation
  double height = 0.0;
  double oldHeight = 0.0;
  double itemHeight = 28.0;
  double itemsCount = 20;
  double screenWidth = 0;
  double imageSize = 100;
  Color appBarColor = COLOR_E_HEAVY_2;
  double fontSizeA = FONT_SIZE_5;
  double fontSizeB = FONT_SIZE_8;
  double iconFontSize = FONT_SIZE_13;
  int colorSize = 250;
  Color barColor = Color.fromRGBO(98, 0, 238, 1);
  double scale = 2;

  onScroll() {
    setState(() {
      height = controller.position.pixels;

      if (height < 30) {
        barColor = Color.fromRGBO(98, 0 + (height * scale).toInt(),
            238 - (height * scale).toInt(), 1);

        fontSizeA = FONT_SIZE_6 - height.Scale(0, 30, 0, 2);
        fontSizeB = FONT_SIZE_8 - height.Scale(0, 30, 0, 6);
        iconFontSize = FONT_SIZE_13 - height.Scale(0, 30, 0, 8);
      }
    });

    oldHeight = height;
  }

  @override
  void dispose() {
    super.dispose();
    controller.removeListener(onScroll);
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      resizeToAvoidBottomInset: false,
      backgroundColor: COLOR_E_HEAVY_2,
      appBar: AppBar(
        toolbarHeight: 0,
        backgroundColor: barColor,
        elevation: 0,
      ),
      body: CustomScrollView(
        dragStartBehavior: DragStartBehavior.down,
        controller: controller,
        physics: ClampingScrollPhysics(),
        slivers: [
          SliverAppBar(
            elevation: 1,
            shape: RoundedRectangleBorder(
              borderRadius: BorderRadius.only(
                bottomLeft: Radius.circular(BORDER_RADIUS_2),
                bottomRight: Radius.circular(BORDER_RADIUS_2),
              ),
            ),
            pinned: true,
            automaticallyImplyLeading: false,
            expandedHeight: 110,
            toolbarHeight: 80,
            floating: true,
            backgroundColor: barColor,
            flexibleSpace: Stack(
              children: <Widget>[
                Container(
                  height: double.infinity,
                  alignment: Alignment.center,
                  margin: EdgeInsets.only(
                    bottom: MAR_PAD_2,
                    top: MAR_PAD_1,
                  ),
                  padding: EdgeInsets.symmetric(horizontal: MAR_PAD_3),
                  child: Row(
                    mainAxisAlignment: MainAxisAlignment.spaceBetween,
                    children: [
                      isUserLoading || userError
                          ? UserNormalCardWithInfoPlaceHolder()
                          : UserNormalCardWithInfo(
                              userModelb,
                              toProfilePage,
                              margin: MAR_PAD_0,
                              fontsizeB: fontSizeB,
                              fontsizeA: fontSizeA,
                              imageSize: double.infinity,
                            ),
                      ElevatedButton(
                        onPressed: () => {buildBottomModals(context)},
                        child: Icon(
                          Icons.grid_view_rounded,
                          color: COLOR_D_HEAVY,
                          size: iconFontSize,
                        ),
                        style: ElevatedButton.styleFrom(
                          shape: CircleBorder(),
                          shadowColor: COLOR_D_HEAVY,
                          padding: EdgeInsets.all(MAR_PAD_2),
                          backgroundColor: COLOR_E_HEAVY_2, // <-- Button color
                          foregroundColor: COLOR_E_HEAVY, // <-- Splash color
                        ),
                      )
                    ],
                  ),
                ),
              ],
            ),
          ),
          SliverList(
            delegate: SliverChildBuilderDelegate(
              childCount: 1,
              (BuildContext context, int index) {
                return Container(
                  padding: EdgeInsets.only(
                      left: MAR_PAD_3, right: MAR_PAD_3, top: MAR_PAD_3),
                  child: Column(
                    children: [
                      IconButtonTypeB("Search Comic", () {
                        setState(() {
                          widget.store.dispatch(SetIndex(0));
                        });
                      }, Icons.search),
                      Container(
                        margin: EdgeInsets.only(
                          bottom: MAR_PAD_3,
                          top: MAR_PAD_2,
                        ),
                        child: Row(
                          crossAxisAlignment: CrossAxisAlignment.center,
                          mainAxisAlignment: MainAxisAlignment.spaceBetween,
                          children: [
                            TitleTypeA(
                              "Trending Comic",
                              color: COLOR_D_LIGHT_1,
                              fontFamily: FONT_FAMILY_C ?? "",
                            ),
                            ElevatedButton(
                              onPressed: () => {
                                //will add
                              },
                              clipBehavior: Clip.hardEdge,
                              child: Icon(
                                Icons.menu,
                                color: COLOR_D_HEAVY,
                                size: FONT_SIZE_12,
                              ),
                              style: ElevatedButton.styleFrom(
                                shape: CircleBorder(),
                                shadowColor: COLOR_E_HEAVY_2,
                                padding: EdgeInsets.all(MAR_PAD_2),
                                backgroundColor:
                                    COLOR_E_HEAVY_2, // <-- Button color
                                foregroundColor:
                                    COLOR_E_HEAVY_2, // <-- Splash color
                              ),
                            )
                          ],
                        ),
                      ),
                      Container(
                        height: 270,
                        alignment: Alignment.center,
                        decoration: BoxDecoration(
                            // border: Border.all(),
                            ),
                        width: MediaQuery.of(context).size.width,
                        child: ListView.builder(
                          itemCount: trendComicList.length == 0
                              ? 5
                              : trendComicList.length,
                          addAutomaticKeepAlives: true,
                          physics: BouncingScrollPhysics(),
                          shrinkWrap: true,
                          scrollDirection: Axis.horizontal,
                          itemBuilder: (BuildContext context, int index) {
                            return isTrendComicsLoading || trendComicsError
                                ? ComicMiniCardPlaceHolder()
                                : ComicMiniCard(
                                    trendComicList[index],
                                    (val) {},
                                    cardHeight: 190,
                                    imageHeight: 160,
                                    cardwidth: 140,
                                  );
                          },
                        ),
                      ),
                      Container(
                        margin: EdgeInsets.only(
                          bottom: MAR_PAD_3,
                          top: MAR_PAD_2,
                        ),
                        child: Row(
                          crossAxisAlignment: CrossAxisAlignment.center,
                          mainAxisAlignment: MainAxisAlignment.spaceBetween,
                          children: [
                            TitleTypeA(
                              "Liked",
                              color: COLOR_D_LIGHT_1,
                              fontFamily: FONT_FAMILY_C ?? "",
                            ),
                            ElevatedButton(
                              onPressed: () => {
                                //will add
                              },
                              clipBehavior: Clip.hardEdge,
                              child: Icon(
                                Icons.menu,
                                color: COLOR_D_HEAVY,
                                size: FONT_SIZE_12,
                              ),
                              style: ElevatedButton.styleFrom(
                                shape: CircleBorder(),
                                shadowColor: COLOR_E_HEAVY_2,
                                padding: EdgeInsets.all(MAR_PAD_2),
                                backgroundColor:
                                    COLOR_E_HEAVY_2, // <-- Button color
                                foregroundColor:
                                    COLOR_E_HEAVY_2, // <-- Splash color
                              ),
                            )
                          ],
                        ),
                      ),
                      Container(
                        height: 200,
                        alignment: Alignment.center,
                        margin: EdgeInsets.symmetric(vertical: MAR_PAD_2),
                        width: MediaQuery.of(context).size.width,
                        child: ListView.builder(
                          padding: EdgeInsets.all(MAR_PAD_1),
                          itemCount: likedComicList.length == 0
                              ? 5
                              : likedComicList.length,
                          physics: BouncingScrollPhysics(),
                          shrinkWrap: true,
                          scrollDirection: Axis.horizontal,
                          itemBuilder: (BuildContext context, int index) {
                            return islikedComicsLoading || likedComicsError
                                ? LikedHorizantalCardPlaceHolder()
                                : LikedHorizantalCard(likedComicList[index]);
                          },
                        ),
                      ),
                    ],
                  ),
                );
              },
            ),
          )
        ],
      ),
    );
  }

  @override
  // TODO: implement wantKeepAlive
  bool get wantKeepAlive => true;
}
